import org.assertj.core.api.Assertions;import org.junit.After;import org.junit.Before;import org.junit.Test;import org.openqa.selenium.JavascriptExecutor;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import org.openqa.selenium.chrome.ChromeDriver;public class WebDriverTest {    private WebDriver driver;    private void clickJavaScript(WebElement element) {        JavascriptExecutor executor = (JavascriptExecutor)driver;        executor.executeScript("arguments[0].click();", element);    }    @Before    public void setup() {        String path = System.getProperty("user.dir") + "/src/test/resources/drivers/chromedriver";        System.setProperty("webdriver.chrome.driver", path);        driver = new ChromeDriver();    }    @Test    public void test() {        driver.get("http://google.com");        String title = driver.getTitle();        Assertions.assertThat(title).isEqualTo("Google");    }    @After    public void tearDown() {        driver.quit();    }}