package junit;import org.assertj.core.api.Assertions;import org.junit.After;import org.junit.Before;import org.junit.Test;import org.openqa.selenium.JavascriptExecutor;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import org.openqa.selenium.chrome.ChromeDriver;import java.util.concurrent.TimeUnit;public class WebDriverTest {    private WebDriver driver;    private void clickJavaScript(WebElement element) {        JavascriptExecutor executor = (JavascriptExecutor)driver;        executor.executeScript("arguments[0].click();", element);    }    @Before    public void setup() {        String chromeDriverName = "chromedriver";        if (!System.getProperty("os.name").contains("Mac")) {            chromeDriverName += "exe";        }        String path = System.getProperty("user.dir") + "/src/test/resources/drivers/" + chromeDriverName;        System.setProperty("webdriver.chrome.driver", path);        driver = new ChromeDriver();        driver.manage().deleteAllCookies();        driver.manage().window().maximize();        driver.manage().timeouts().pageLoadTimeout(30, TimeUnit.SECONDS);        driver.manage().timeouts().implicitlyWait(5, TimeUnit.SECONDS);    }    @Test    public void test() {        openGooglePage();    }    private void openGooglePage() {        driver.get("http://google.com");        String title = driver.getTitle();        Assertions.assertThat(title).isEqualTo("Google");    }    @After    public void tearDown() {        driver.quit();    }}